/* NoteStyles.css */

.note-container {
    padding: 10px;
    margin: 20px 0;
    border: 1px solid #ccc;
    border-radius: 5px;
}

.note-title {
    color: #333;
}

.note-content {
    color: #666;
}

.note-date {
    color: #999;
    font-size: 0.8rem;
}

.delete-button {
    background-color: #f44336;
    /* Red */
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s;
}

.delete-button:hover {
    background-color: #d32f2f;
    /* Darker red */
}
